CREATE DATABASE db__ecomerceUSE db_ecomerceCREATE TABLE tb_Usuarios(	Id_Usuarios INT PRIMARY KEY NOT NULL,	Nome VARCHAR(100) NULL,	Email VARCHAR(50) NULL,	Senha VARCHAR(100) NULL,	endereco VARCHAR(50) NULL,	forma_pagamento VARCHAR (100) NULL)CREATE TABLE tb_Produtos(Id_Produtos INT PRIMARY KEY NOT NULL,Preco FLOAT  NULL,Quantidade INT  NULL,Categoria VARCHAR(50) NULL,)CREATE TABLE tb_Entregas(Id_Entrega INT PRIMARY KEY NOT NULL,PrevisaoDeEntrega VARCHAR(50)  NULL,FK_Id_Usuarios INT  NULL,FK_Id_Produtos INT  NULL,FOREIGN KEY (FK_Id_Usuarios) REFERENCES tb_Usuarios (Id_Usuarios),FOREIGN KEY (FK_Id_Produtos) REFERENCES tb_Produtos (Id_Produtos))
